source:
.login-indicator {
    display: inline-flex;
    align-items: center;
    gap: 0.25rem;
    font-size: 0.7rem;
    min-height: 48px;
    padding: 0 0.15rem;
  }

  .login-user {
    color: var(--color-text-muted, #565f89);
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: 100px;
  }

  .login-logout-btn {
    background: transparent;
    border: none;
    color: var(--color-text-muted, #565f89);
    cursor: pointer;
    font-size: 0.7rem;
    padding: 0.15rem 0.3rem;
    border-radius: 3px;
    line-height: 1;
    min-width: 20px;
    min-height: 20px;
    display: inline-flex;
    align-items: center;
    justify-content: center;
  }

  .login-logout-btn:hover {
    background: var(--color-surface-hover, #2f3549);
    color: #f7768e;
  }

  .login-link {
    color: var(--color-text-muted, #565f89);
    text-decoration: none;
    white-space: nowrap;
    font-size: 0.7rem;
    min-height: 48px;
    display: inline-flex;
    align-items: center;
    padding: 0 0.15rem;
  }

  .login-link:hover {
    color: var(--color-accent, #7aa2f7);
  }

  /* Hide on very small screens to keep header clean */
  @media (max-width: 360px) {
    .login-user {
      max-width: 60px;
    }
  }
