.code-block-wrapper {
    position: relative;
    margin: var(--spacing-sm) 0;
  }

  .copy-button {
    position: absolute;
    top: 0.5rem;
    right: 0.5rem;
    display: flex;
    align-items: center;
    gap: 0.25rem;
    padding: 0.25rem 0.5rem;
    background: var(--color-surface);
    border: 1px solid var(--color-border);
    border-radius: 4px;
    color: var(--color-text-muted);
    font-size: 0.75rem;
    font-family: var(--font-sans);
    cursor: pointer;
    transition: all 0.2s ease;
    opacity: 0;
  }

  .code-block-wrapper:hover .copy-button {
    opacity: 1;
  }

  .copy-button:hover {
    background: var(--color-accent);
    color: var(--color-bg);
    border-color: var(--color-accent);
  }

  .copy-button.copied {
    background: var(--color-accent);
    color: var(--color-bg);
    border-color: var(--color-accent);
  }

  .copy-button svg {
    width: 14px;
    height: 14px;
  }

  /* Always show on mobile/touch devices */
  @media (hover: none) {
    .copy-button {
      opacity: 0.7;
    }
  }
