@import"https://fonts.googleapis.com/css2?family=LXGW+WenKai+TC&display=swap";
@font-face{font-family:"Virgil";src:url(https://excalidraw.com/Virgil.woff2) format("woff2");font-display:swap}
.mermaid-wrapper[data-astro-cid-qz3dz2lk]{position:relative;margin:1.5rem 0;border:1px solid var(--border-color, #e0d8c8);border-radius:8px;overflow:hidden;background:var(--code-bg, #fdf6e3)}
.mermaid-scroll[data-astro-cid-qz3dz2lk]{overflow-x:auto;overflow-y:hidden;-webkit-overflow-scrolling:touch;padding:1rem;min-height:100px;display:flex;justify-content:center}
.mermaid-render[data-astro-cid-qz3dz2lk]{min-width:fit-content}
.mermaid-render[data-astro-cid-qz3dz2lk] svg{max-width:none;height:auto}
.mermaid-caption[data-astro-cid-qz3dz2lk]{text-align:center;font-size:0.85rem;color:var(--text-muted, #93a1a1);margin:0;padding:0.5rem 1rem 0.75rem;border-top:1px solid var(--border-color, #e0d8c8)}
.mermaid-expand-btn[data-astro-cid-qz3dz2lk]{position:absolute;top:8px;right:8px;background:var(--code-bg, #fdf6e3);border:1px solid var(--border-color, #e0d8c8);border-radius:4px;padding:4px 6px;cursor:pointer;opacity:0.6;transition:opacity 0.2s;color:var(--text-color, #657b83);z-index:1}
.mermaid-expand-btn[data-astro-cid-qz3dz2lk]:hover{opacity:1}
.mermaid-overlay[data-astro-cid-qz3dz2lk]{position:fixed;inset:0;z-index:9999;background:#fdf6e3;display:flex;align-items:center;justify-content:center;overflow:auto;-webkit-overflow-scrolling:touch;touch-action:pan-x pan-y pinch-zoom}
.mermaid-overlay-content[data-astro-cid-qz3dz2lk]{padding:2rem;max-width:95vw;max-height:95vh;overflow:auto}
.mermaid-overlay-content[data-astro-cid-qz3dz2lk] svg{max-width:none;height:auto}
.mermaid-close-btn[data-astro-cid-qz3dz2lk]{position:fixed;top:16px;right:16px;z-index:10000;background:rgba(0,0,0,0.1);border:none;color:#657b83;font-size:1.5rem;width:40px;height:40px;border-radius:50%;cursor:pointer;display:flex;align-items:center;justify-content:center}
.mermaid-close-btn[data-astro-cid-qz3dz2lk]:hover{background:rgba(0,0,0,0.2)}
.mermaid-render[data-astro-cid-qz3dz2lk]:empty::after{content:"載入圖表中...";color:var(--text-muted, #93a1a1);font-size:0.85rem}
