(function(){const darkTheme = "https://gu-log.vercel.app/giscus-tokyo-night.css";
const lightTheme = "https://gu-log.vercel.app/giscus-solarized-light.css";

  // Sync giscus theme with site theme toggle using custom CSS URLs
  function getGiscusThemeUrl() {
    const theme = document.documentElement.getAttribute('data-theme') || 'dark';
    return theme === 'dark' ? darkTheme : lightTheme;
  }

  function updateGiscusTheme() {
    const iframe = document.querySelector('iframe.giscus-frame');
    if (iframe) {
      iframe.contentWindow?.postMessage(
        { giscus: { setConfig: { theme: getGiscusThemeUrl() } } },
        'https://giscus.app'
      );
    }
  }

  // Watch for theme changes
  const observer = new MutationObserver((mutations) => {
    for (const mutation of mutations) {
      if (mutation.attributeName === 'data-theme') {
        updateGiscusTheme();
      }
    }
  });
  observer.observe(document.documentElement, { attributes: true });

  // Set initial theme once giscus loads
  window.addEventListener('message', (event) => {
    if (event.origin !== 'https://giscus.app') return;
    if (typeof event.data !== 'object' || !event.data.giscus) return;
    updateGiscusTheme();
  });
})();
