---
/**
 * Theme Toggle - Switch between dark and solarized light
 * Using minimal SVG icons instead of emoji
 */
---

<button class="theme-toggle" id="theme-toggle" aria-label="Toggle theme">
  <svg
    class="icon-sun"
    xmlns="http://www.w3.org/2000/svg"
    width="16"
    height="16"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    stroke-width="2"
    stroke-linecap="round"
    stroke-linejoin="round"
  >
    <circle cx="12" cy="12" r="5"></circle>
    <line x1="12" y1="1" x2="12" y2="3"></line>
    <line x1="12" y1="21" x2="12" y2="23"></line>
    <line x1="4.22" y1="4.22" x2="5.64" y2="5.64"></line>
    <line x1="18.36" y1="18.36" x2="19.78" y2="19.78"></line>
    <line x1="1" y1="12" x2="3" y2="12"></line>
    <line x1="21" y1="12" x2="23" y2="12"></line>
    <line x1="4.22" y1="19.78" x2="5.64" y2="18.36"></line>
    <line x1="18.36" y1="5.64" x2="19.78" y2="4.22"></line>
  </svg>
  <svg
    class="icon-moon"
    xmlns="http://www.w3.org/2000/svg"
    width="16"
    height="16"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    stroke-width="2"
    stroke-linecap="round"
    stroke-linejoin="round"
  >
    <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z"></path>
  </svg>
</button>

<script>
  const toggle = document.getElementById('theme-toggle');

  function getPreferredTheme(): 'dark' | 'light' {
    const saved = localStorage.getItem('theme');
    if (saved === 'light' || saved === 'dark') return saved;
    return 'dark';
  }

  function setTheme(theme: 'dark' | 'light') {
    document.documentElement.setAttribute('data-theme', theme);
    localStorage.setItem('theme', theme);
  }

  setTheme(getPreferredTheme());

  toggle?.addEventListener('click', () => {
    const current = document.documentElement.getAttribute('data-theme');
    setTheme(current === 'dark' ? 'light' : 'dark');
    // Remove focus to prevent stuck state
    (toggle as HTMLButtonElement).blur();
  });
</script>

<style>
  .theme-toggle {
    background: transparent;
    border: none;
    padding: 0.25rem;
    cursor: pointer;
    line-height: 1;
    transition:
      color 0.2s ease,
      opacity 0.2s ease;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--color-text-muted);
    outline: none;
    -webkit-tap-highlight-color: transparent;
  }

  .theme-toggle:hover {
    color: var(--color-text);
  }

  .theme-toggle:focus {
    outline: none;
  }

  .theme-toggle:active {
    opacity: 0.7;
  }

  .icon-sun,
  .icon-moon {
    display: none;
  }

  :global([data-theme='dark']) .icon-moon {
    display: block;
  }

  :global([data-theme='light']) .icon-sun {
    display: block;
  }

  @media (pointer: coarse) {
    .theme-toggle {
      min-width: 32px;
      min-height: 32px;
    }
  }
</style>
