/* ─── AI Popup Base ─── */
  .ai-popup {
    position: absolute;
    z-index: 9999;
    background: var(--color-surface, #24283b);
    border: 1px solid var(--color-border, #3b4261);
    border-radius: 10px;
    padding: 0.4rem;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.3);
    display: flex;
    gap: 0.25rem;
    animation: ai-popup-fadein 0.15s ease-out;
    max-width: 90vw;
  }

  @keyframes ai-popup-fadein {
    from {
      opacity: 0;
      transform: translateY(4px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  /* ─── Desktop ─── */
  .ai-popup--desktop {
    position: absolute;
  }

  /* ─── Mobile Bottom Sheet ─── */
  .ai-popup--mobile {
    position: fixed;
    bottom: 0;
    left: 0;
    right: 0;
    top: auto;
    border-radius: 14px 14px 0 0;
    padding: 0.75rem;
    max-width: 100vw;
    box-shadow: 0 -4px 20px rgba(0, 0, 0, 0.4);
    animation: ai-popup-slideup 0.2s ease-out;
  }

  @keyframes ai-popup-slideup {
    from {
      transform: translateY(100%);
    }
    to {
      transform: translateY(0);
    }
  }

  /* ─── Buttons ─── */
  .ai-popup-btn {
    border: none;
    background: transparent;
    color: var(--color-text, #a9b1d6);
    font-size: 0.85rem;
    padding: 0.4rem 0.75rem;
    border-radius: 6px;
    cursor: pointer;
    white-space: nowrap;
    font-family: inherit;
    transition: background 0.15s;
  }

  .ai-popup-btn:hover {
    background: var(--color-surface-hover, #2f3549);
  }

  .ai-popup-btn--login {
    color: var(--color-accent, #7aa2f7);
    font-weight: 500;
  }

  .ai-popup-btn--ask {
    color: var(--color-accent, #7aa2f7);
  }

  .ai-popup-btn--edit {
    color: #9ece6a;
  }

  .ai-popup-btn--confirm {
    color: #9ece6a;
    font-weight: 500;
  }

  .ai-popup-btn--cancel {
    color: var(--color-text-muted, #565f89);
  }

  /* ─── Loading ─── */
  .ai-popup-loading {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.5rem 0.75rem;
    color: var(--color-text-muted, #565f89);
    font-size: 0.85rem;
  }

  .ai-popup-spinner {
    width: 16px;
    height: 16px;
    border: 2px solid var(--color-border, #3b4261);
    border-top-color: var(--color-accent, #7aa2f7);
    border-radius: 50%;
    animation: ai-spin 0.7s linear infinite;
    flex-shrink: 0;
  }

  @keyframes ai-spin {
    to { transform: rotate(360deg); }
  }

  /* ─── Result Panel ─── */
  .ai-popup-result {
    position: relative;
    padding: 0.5rem;
    min-width: 280px;
    max-width: min(500px, 90vw);
    max-height: 60vh;
    overflow-y: auto;
  }

  .ai-popup-close {
    position: absolute;
    top: 0.25rem;
    right: 0.25rem;
    background: transparent;
    border: none;
    color: var(--color-text-muted, #565f89);
    font-size: 1rem;
    cursor: pointer;
    padding: 0.25rem 0.5rem;
    border-radius: 4px;
    line-height: 1;
  }

  .ai-popup-close:hover {
    background: var(--color-surface-hover, #2f3549);
    color: var(--color-text, #a9b1d6);
  }

  .ai-popup-result-body {
    margin-top: 1.5rem;
    font-size: 0.9rem;
    line-height: 1.6;
    color: var(--color-text, #a9b1d6);
    white-space: pre-wrap;
    word-break: break-word;
  }

  .ai-popup-result--error .ai-popup-error-text {
    margin-top: 1.5rem;
    color: #f7768e;
    font-size: 0.85rem;
  }

  /* ─── Diff Display ─── */
  .ai-popup-diff {
    margin-top: 1.5rem;
    font-family: var(--font-mono, monospace);
    font-size: 0.8rem;
    line-height: 1.5;
    background: var(--color-bg, #1a1b26);
    border-radius: 6px;
    padding: 0.5rem;
    overflow-x: auto;
    max-height: 40vh;
    overflow-y: auto;
  }

  .ai-popup-diff-line {
    white-space: pre;
    padding: 0 0.5rem;
  }

  .ai-popup-diff-add {
    background: rgba(158, 206, 106, 0.15);
    color: #9ece6a;
  }

  .ai-popup-diff-remove {
    background: rgba(247, 118, 142, 0.15);
    color: #f7768e;
  }

  .ai-popup-actions {
    display: flex;
    gap: 0.5rem;
    margin-top: 0.75rem;
    padding-top: 0.5rem;
    border-top: 1px solid var(--color-border, #3b4261);
  }

  /* ─── Committed ─── */
  .ai-popup-committed {
    margin-top: 1.5rem;
    font-size: 0.9rem;
    color: #9ece6a;
  }

  .ai-popup-committed code {
    font-family: var(--font-mono, monospace);
    background: var(--color-bg, #1a1b26);
    padding: 0.15em 0.4em;
    border-radius: 4px;
    font-size: 0.85em;
  }

  .ai-popup-committed-icon {
    font-size: 1.1em;
  }

  /* ─── Light Theme Overrides ─── */
  [data-theme='light'] .ai-popup {
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
  }

  [data-theme='light'] .ai-popup-btn--edit {
    color: #268b79;
  }

  [data-theme='light'] .ai-popup-btn--confirm {
    color: #268b79;
  }

  [data-theme='light'] .ai-popup-diff-add {
    background: rgba(38, 139, 121, 0.1);
    color: #268b79;
  }

  [data-theme='light'] .ai-popup-diff-remove {
    background: rgba(220, 50, 47, 0.1);
    color: #dc322f;
  }

  [data-theme='light'] .ai-popup-committed {
    color: #268b79;
  }

  [data-theme='light'] .ai-popup-result--error .ai-popup-error-text {
    color: #dc322f;
  }

  /* ─── Mobile adjustments ─── */
  @media (max-width: 639px) {
    .ai-popup-result {
      min-width: unset;
      max-width: 100%;
    }
  }
