/* Global Styles - Mobile First */
/* Themes: Tokyo Night (default) + Solarized Light */
:root {
  /* Tokyo Night theme (default) */
  --color-bg: #1a1b26;
  --color-surface: #24283b;
  --color-surface-hover: #2f3549;
  --color-text: #a9b1d6;
  --color-text-muted: #565f89;
  --color-accent: #7aa2f7;
  --color-border: #3b4261;

  /* Common */
  --font-sans:
    'Inter', 'Noto Sans TC', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  --font-mono: 'JetBrains Mono', 'Fira Code', monospace;
  --max-width: 680px;
  --spacing-xs: 0.5rem;
  --spacing-sm: 1rem;
  --spacing-md: 1.5rem;
  --spacing-lg: 2rem;
  --spacing-xl: 3rem;
}
/* Solarized Light theme */
[data-theme='light'] {
  --color-bg: #fdf6e3;
  --color-surface: #eee8d5;
  --color-surface-hover: #e5dfc9;
  --color-text: #657b83;
  --color-text-muted: #93a1a1;
  --color-accent: #268bd2;
  --color-border: #d3cbb7;
}
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html {
  font-size: 16px;
  scroll-behavior: smooth;
  scroll-padding-top: 80px;
}
body {
  font-family: var(--font-sans);
  background: var(--color-bg);
  color: var(--color-text);
  line-height: 1.7;
  padding: var(--spacing-sm);
  min-height: 100vh;
  transition:
    background 0.3s ease,
    color 0.3s ease;

  /* CJK typography improvements */
  text-spacing: trim-start allow-end trim-adjacent ideograph-alpha ideograph-numeric;
  -webkit-text-size-adjust: 100%;
  text-rendering: optimizeLegibility;
}
/* Typography */
h1,
h2,
h3,
h4,
h5,
h6 {
  line-height: 1.3;
  margin-bottom: var(--spacing-sm);
  color: var(--color-text);
}
h1 {
  font-size: 1.75rem;
}
h2 {
  font-size: 1.4rem;
  margin-top: var(--spacing-lg);
}
h3 {
  font-size: 1.2rem;
  margin-top: var(--spacing-md);
}
p {
  margin-bottom: var(--spacing-sm);
  word-break: break-word;
  overflow-wrap: break-word;
}
/* CJK + Latin mixed typography improvements */
p,
li,
blockquote {
  /* Better spacing for CJK characters */
  letter-spacing: 0.02em;
  word-spacing: 0.05em;
}
/* Hanging punctuation for Chinese quotes */
[lang='zh-TW'] p,
[lang='zh-TW'] li {
  hanging-punctuation: first last;
}
/* Adjust spacing between CJK and Latin */
[lang='zh-TW'] {
  text-autospace: ideograph-alpha ideograph-numeric;
}
/* Better line height for CJK text */
[lang='zh-TW'] p,
[lang='zh-TW'] li {
  line-height: 1.8;
}
/* Tighter letter spacing for English to distinguish from CJK */
[lang='en'] p,
[lang='en'] li {
  letter-spacing: normal;
  line-height: 1.7;
}
a {
  color: var(--color-accent);
  text-decoration: none;
}
a:hover {
  text-decoration: underline;
}
/* Container */
.container {
  max-width: var(--max-width);
  margin: 0 auto;
  padding: var(--spacing-sm);
}
/* Code */
code {
  font-family: var(--font-mono);
  background: var(--color-surface);
  padding: 0.2em 0.4em;
  border-radius: 4px;
  font-size: 0.9em;
}
pre {
  background: var(--color-surface);
  padding: var(--spacing-sm);
  border-radius: 8px;
  overflow-x: auto;
  margin: var(--spacing-sm) 0;
}
pre code {
  background: none;
  padding: 0;
}
/* Shiki theme switching - hide light theme in dark mode and vice versa */
:root pre[class*='shiki'] {
  background: var(--color-surface) !important;
}
:root .shiki.light,
:root .shiki.light span {
  color: var(--shiki-light) !important;
  background-color: var(--shiki-light-bg) !important;
}
:root .shiki.dark,
:root .shiki.dark span {
  color: var(--shiki-dark) !important;
  background-color: var(--shiki-dark-bg) !important;
}
/* Hide light theme in dark mode (default) */
:root .shiki.light {
  display: none;
}
:root .shiki.dark {
  display: block;
}
/* Hide dark theme in light mode */
[data-theme='light'] .shiki.light {
  display: block;
}
[data-theme='light'] .shiki.dark {
  display: none;
}
/* Lists */
ul,
ol {
  margin-left: var(--spacing-md);
  margin-bottom: var(--spacing-sm);
}
li {
  margin-bottom: var(--spacing-xs);
}
/* Blockquote */
blockquote {
  border-left: 3px solid var(--color-accent);
  padding-left: var(--spacing-sm);
  color: var(--color-text-muted);
  margin: var(--spacing-sm) 0;
  font-style: italic;
}
/* ClawdNote - Clawd's commentary blockquote */
blockquote.claude-note {
  background: var(--color-surface);
  border-left-color: #cb7551; /* Clawd orange */
  padding: 0.75rem 1rem;
  border-radius: 0 8px 8px 0;
  font-style: normal;
  color: var(--color-text);
  margin: var(--spacing-md) 0;
}
blockquote.claude-note strong {
  color: #cb7551;
}
blockquote.claude-note br {
  display: block;
  content: '';
  margin-top: 0.5rem;
}
/* Citation / Source */
.source-citation {
  background: var(--color-surface);
  border-radius: 8px;
  padding: var(--spacing-sm);
  margin-bottom: var(--spacing-md);
  border-left: 3px solid var(--color-accent);
}
.source-citation a {
  word-break: break-all;
}
/* Article meta */
.post-meta {
  color: var(--color-text-muted);
  font-size: 0.9rem;
  margin-bottom: var(--spacing-md);
}
/* Broken Link Annotation (SQAA Level 6) */
.broken-link {
  text-decoration: line-through;
  color: #999;
}
.broken-link-notice {
  font-size: 0.8em;
  color: #c47a00;
  background: #fff3cd;
  padding: 2px 6px;
  border-radius: 4px;
  margin-left: 4px;
}
[data-theme='light'] .broken-link-notice {
  color: #856404;
  background: #fff3cd;
}
/* Responsive */
@media (min-width: 640px) {
  body {
    padding: var(--spacing-md);
  }

  h1 {
    font-size: 2rem;
  }
  h2 {
    font-size: 1.5rem;
  }
}
