/* Trigger button in header */
  .search-trigger {
    background: transparent;
    border: none;
    padding: 0.25rem;
    cursor: pointer;
    line-height: 1;
    transition:
      color 0.2s ease,
      opacity 0.2s ease;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--color-text-muted);
    outline: none;
    -webkit-tap-highlight-color: transparent;
  }

  .search-trigger:hover {
    color: var(--color-text);
  }

  .search-trigger:focus {
    outline: none;
  }

  .search-trigger:active {
    opacity: 0.7;
  }

  @media (pointer: coarse) {
    .search-trigger {
      min-width: 32px;
      min-height: 32px;
    }
  }

  /* Modal overlay */
  .search-modal-overlay {
    position: fixed;
    inset: 0;
    background: rgba(0, 0, 0, 0.5);
    backdrop-filter: blur(4px);
    z-index: 1000;
    display: flex;
    align-items: flex-start;
    justify-content: center;
    padding-top: 12vh;
    opacity: 0;
    visibility: hidden;
    transition:
      opacity 0.2s ease,
      visibility 0.2s ease;
  }

  .search-modal-overlay[aria-hidden='false'] {
    opacity: 1;
    visibility: visible;
  }

  /* Modal container */
  .search-modal {
    width: 90%;
    max-width: 480px;
    background: var(--color-bg);
    border: 1px solid var(--color-border);
    border-radius: 12px;
    box-shadow: 0 16px 48px rgba(0, 0, 0, 0.25);
    overflow: hidden;
    transform: scale(0.95) translateY(-10px);
    transition: transform 0.2s ease;
  }

  .search-modal-overlay[aria-hidden='false'] .search-modal {
    transform: scale(1) translateY(0);
  }

  /* Modal header with input */
  .search-modal-header {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    padding: 1rem 1.25rem;
    border-bottom: 1px solid var(--color-border);
  }

  .search-modal-icon {
    color: var(--color-text-muted);
    flex-shrink: 0;
  }

  .search-modal-input {
    flex: 1;
    border: none;
    background: transparent;
    color: var(--color-text);
    font-size: 1rem;
    outline: none;
  }

  .search-modal-input::placeholder {
    color: var(--color-text-muted);
  }

  .search-shortcut {
    font-size: 0.7rem;
    padding: 0.2rem 0.4rem;
    background: var(--color-bg-muted);
    border: 1px solid var(--color-border);
    border-radius: 4px;
    color: var(--color-text-muted);
    flex-shrink: 0;
  }

  /* Search results */
  .search-results {
    max-height: 360px;
    overflow-y: auto;
  }

  .search-results:empty {
    display: none;
  }

  /* Individual result item â€” card-like with clear separation */
  .search-result-item {
    display: block;
    padding: 0.875rem 1.25rem;
    margin: 6px 8px;
    text-decoration: none;
    border: 1px solid var(--color-border, #e0e0e0);
    border-radius: 8px;
    transition:
      background 0.15s ease,
      border-color 0.15s ease;
  }

  .search-result-item:first-child {
    margin-top: 8px;
  }

  .search-result-item:last-child {
    margin-bottom: 8px;
  }

  .search-result-item:hover,
  .search-result-item.selected {
    background: var(--color-bg-muted);
    border-color: var(--color-accent);
  }

  /* Result header with ticket ID and date */
  .search-result-header {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    margin-bottom: 0.375rem;
  }

  .search-result-ticket {
    display: inline-block;
    padding: 0.125rem 0.5rem;
    background: var(--color-accent);
    color: white;
    font-size: 0.7rem;
    font-weight: 600;
    border-radius: 4px;
    font-family: 'SF Mono', 'Monaco', 'Consolas', monospace;
  }

  .search-result-date {
    color: var(--color-text-muted);
    font-size: 0.7rem;
  }

  /* Result title */
  .search-result-title {
    color: var(--color-text);
    font-size: 0.95rem;
    font-weight: 600;
    margin-bottom: 0.3rem;
    line-height: 1.4;
    /* Allow 2 lines */
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  /* Result source */
  .search-result-source {
    color: var(--color-text-muted);
    font-size: 0.72rem;
  }

  .search-no-results {
    padding: 1.5rem;
    text-align: center;
    color: var(--color-text-muted);
    font-size: 0.9rem;
  }
